import {
  getAuth,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
} from "firebase/auth";
import { ref, onValue, set, push, remove, off } from "firebase/database";
import React, { useEffect, useState, useContext } from "react";
import {
  StyleSheet,
  Text,
  View,
  KeyboardAvoidingView,
  TextInput,
  TouchableOpacity,
  Dimensions,
} from "react-native";

import AppContext from "../components/AppContext";
import { auth, database } from "../firebase";

const { height } = Dimensions.get("window");

const saveUser = async ({ uid, email, id, firstName, lastName }) => {
  const db = database;
  const reference1 = ref(db, "/users/" + uid);
  set(reference1, {
    id,
    email,
    firstName,
    lastName,
  });
};
const gotoApp = ({
  initMyUID,
  initMyID,
  initMyFirstName,
  initMyLastName,
  initMyEmail,
  startTutorial,
  navigation,
}) => {
  initMyUID();
  initMyID();
  initMyFirstName();
  initMyLastName();
  initMyEmail();
  if (!startTutorial) {
    navigation.navigate("Tabs");
  }
  //startTutorial 이 true라면 afterScreen.js로 이동필요
};
const RegisterScreen3 = ({ navigation, route }) => {
  const { uid, email } = route.params;

  const [id, setId] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [valid, setValid] = useState(false);

  const myContext = useContext(AppContext);
  const startTutorial = false;
  const initMyUID = () => {
    myContext.initMyUID(uid);
  };
  const initMyID = () => {
    myContext.initMyID(id);
  };
  const initMyFirstName = () => {
    myContext.initMyFirstName(firstName);
  };
  const initMyLastName = () => {
    myContext.initMyLastName(lastName);
  };
  const initMyEmail = () => {
    myContext.initMyEmail(email);
  };

  const handleSignUp = () => {
    saveUser({ uid, email, id, firstName, lastName });
    gotoApp({
      initMyUID,
      initMyID,
      initMyFirstName,
      initMyLastName,
      initMyEmail,
      startTutorial,
      navigation,
    });
  };

  const ContinueButton = () => {
    useEffect(() => {
      setValid(false);
      if (id.length !== 0) {
        if (firstName.length !== 0) {
          if (lastName.length !== 0) {
            setValid(true);
          }
        }
      }
    }, [id, firstName, lastName]);
    if (valid) {
      return (
        <View style={styles.buttonContainer}>
          <TouchableOpacity onPress={handleSignUp} style={styles.button}>
            <View style={{ justifyContent: "center", alignItems: "center" }}>
              <Text style={styles.buttonText}>계속하기</Text>
            </View>
          </TouchableOpacity>
        </View>
      );
    } else {
      return (
        <View style={styles.buttonContainer}>
          <TouchableOpacity style={styles.invalidButton}>
            <View style={{ justifyContent: "center", alignItems: "center" }}>
              <Text style={styles.invalidButtonText}>계속하기</Text>
            </View>
          </TouchableOpacity>
        </View>
      );
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.inputContainer}>
        <Text style={{ fontSize: 13, marginLeft: 14, marginTop: 20 }}>
          아이디를 입력해주세요
        </Text>
        <View
          style={{
            borderBottomColor: "#ADB1C5",
            borderBottomWidth: 1,
            marginTop: 5,
            paddingVertical: 10,
            marginHorizontal: 10,
          }}
        >
          <TextInput
            placeholder="mapsee"
            value={id}
            onChangeText={(text) => setId(text)}
            style={styles.input}
          />
        </View>
        <Text style={{ fontSize: 13, marginLeft: 14, marginTop: 20 }}>
          이름을 입력해주세요
        </Text>
        <View
          style={{
            borderBottomColor: "#ADB1C5",
            borderBottomWidth: 1,
            marginTop: 5,
            paddingVertical: 10,
            marginHorizontal: 10,
          }}
        >
          <TextInput
            placeholder="이름"
            value={firstName}
            onChangeText={(text) => setFirstName(text)}
            style={styles.input}
          />
          <TextInput
            placeholder="성"
            value={lastName}
            onChangeText={(text) => setLastName(text)}
            style={styles.input}
          />
        </View>
      </View>
      <ContinueButton />
    </View>
  );
};

export default RegisterScreen3;

const styles = StyleSheet.create({
  container: {
    height: height * 1.1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "white",
  },

  textContainer: {
    fontWeight: "600",
    fontSize: 20,
    marginBottom: 20,
    alignItems: "flex-start",
    marginLeft: 30,
  },

  inputContainer: {
    flex: 0.7,
    width: "90%",
  },

  input: {
    backgroundColor: "white",
    borderRadius: 10,
  },

  buttonContainer: {
    width: "80%",
    justifyContent: "flex-end",
    alignItems: "center",
    marginTop: 100,
  },

  button: {
    backgroundColor: "#00CCBD",
    width: "100%",
    padding: 15,
    borderRadius: 10,
  },

  invalidButton: {
    backgroundColor: "white",
    width: "100%",
    padding: 15,
    borderRadius: 10,
    borderColor: "black",
    borderWidth: 1,
  },

  buttonOutline: {
    backgroundColor: "white",
    marginTop: 5,
    borderColor: "#0782F9",
    borderWidth: 2,
  },

  buttonText: {
    color: "white",
    fontWeight: "700",
    fontSize: 16,
  },

  invalidButtonText: {
    color: "black",
    fontWeight: "700",
    fontSize: 16,
  },

  buttonOutlineText: {
    color: "#0782F9",
    fontWeight: "700",
    fontSize: 16,
  },
});
